INSERT INTO `wp_kboard_board_content` VALUES (4,1,0,1,'Jaehee','MMS Client API was updated to beta-0.6.1.','MMS Client API was updated to beta-0.6.1. \nIt is available now. See <a href=\"https://github.com/HaJaehee/MC_MMS/tree/beta-0.6.1-alpha+active\" target=\"_blank\" rel=\"noreferrer\">https://github.com/HaJaehee/MC_MMS/tree/beta-0.6.1-alpha+active</a>.\nIf you are using MMS Client API beta-0.6.0, need not update the API. Because it is compatible with MMS server beta-0.6.1.\nThanks.','20171121161407','20180823173355',87,0,0,0,0,'','','','','','','1','trash','');
INSERT INTO `wp_kboard_board_content` VALUES (5,1,0,1,'Jaehee','We restored log for debugging function. Available from 11:00 a.m., 20-Nov (KST).','We restored log for debugging function. \nIt had critical bug related to specific service.\nWe has fixed the problem.\nAvailable from 11:00 a.m., 20-Nov (Korea Standard Time).\nThanks.','20171121161938','20180823173355',64,0,1,0,1,'','','','','','','1','trash','');
INSERT INTO `wp_kboard_board_content` VALUES (6,1,0,1,'Jaehee','We reset MMS server at 22:20 on 21-Nov(KST) in order to resolve an issue.','We reset MMS server at 22:20 on 21-Nov (KST) in order to resolve an issue.\nIt was that Monitoring service is not able to remove an ID item in IDs being debugged list.\nAt 22:20 (KST), we resolved it.\nWe did not reset MNS dummy.\nThanks.','20171121172637','20180823173355',76,0,1,0,1,'','','','','','','1','trash','');
INSERT INTO `wp_kboard_board_content` VALUES (7,1,0,1,'Jaehee','Please let us know that you make polling requests at intervals less than one second.','We found that some service consumers make polling requests at intervals of less than one second.\nIf you have to use that, please let us know which service is. \nThanks.','20171121232542','20180823173355',72,0,0,0,0,'','','','','','true','1','trash','');
INSERT INTO `wp_kboard_board_content` VALUES (8,1,0,1,'Jaehee','We reset MMS server at 18:34 on 22-Nov(KST) in order to resolve fatal issue.','We reset MMS server at 18:34 on 21-Nov(KST) in order to resolve fatal issue.\nIt was caused by duplicated values, which must not be duplicated, in the class implemented in order to provide log for debugging.\nWe resolved it. Therefore you can use MMS normally.\nWe are sorry for your inconvenience.\nThanks.','20171122185348','20180823173355',78,0,1,0,1,'','','','','','','1','trash','');
