INSERT INTO `wp_posts` VALUES (51,48,'2017-06-22 12:24:07','2017-06-22 03:24:07','<b>Log level: info</b>\r\n<br/><br/>\r\n[insert_php]\r\n\r\n$directory = \"/var/mms/logs/\";\r\n\r\n$myfile = fopen($directory.\"mms-server.log\", \"r\") or die(\"Unable to open file!\");\r\nif ($myfile ) {\r\nwhile (($line = fgets($myfile )) !== false) {\r\n// process the line read.\r\n\r\nif ((strpos($line, \'INFO\') !== false) || (strpos($line, \'WARN\') !== false) || (strpos($line, \'ERROR\') !== false)) { echo $line; }\r\n}\r\n\r\nfclose($myfile );\r\n} else {\r\n// error opening the file.\r\n}\r\n\r\n[/insert_php]','Logging page','','publish','closed','closed','','logging-page','','','2018-08-23 17:40:43','2018-08-23 08:40:43','',0,'http://192.168.0.5/?page_id=51',0,'page','',0);
