INSERT INTO `wp_posts` VALUES (470,48,'2017-11-21 17:26:37','2017-11-21 08:26:37','We reset MMS server at 22:20 on 21-Nov (KST) in order to resolve an issue.\nIt was that Monitoring service is not able to remove an ID item in IDs being debugged list.\nAt 22:20 (KST), we resolved it.\nWe did not reset MNS dummy.\nThanks.','We reset MMS server at 22:20 on 21-Nov(KST) in order to resolve an issue.','','publish','closed','closed','','6','','','2018-08-23 17:33:55','2018-08-23 08:33:55','',1,'https://143.248.57.144/?kboard_content_redirect=6',0,'kboard','',0);
