INSERT INTO `wp_posts` VALUES (525,48,'2018-08-23 17:40:43','2018-08-23 08:40:43','<b>Log level: info</b>\r\n<br/><br/>\r\n[insert_php]\r\n\r\n$directory = \"/var/mms/logs/\";\r\n\r\n$myfile = fopen($directory.\"mms-server.log\", \"r\") or die(\"Unable to open file!\");\r\nif ($myfile ) {\r\nwhile (($line = fgets($myfile )) !== false) {\r\n// process the line read.\r\n\r\nif ((strpos($line, \'INFO\') !== false) || (strpos($line, \'WARN\') !== false) || (strpos($line, \'ERROR\') !== false)) { echo $line; }\r\n}\r\n\r\nfclose($myfile );\r\n} else {\r\n// error opening the file.\r\n}\r\n\r\n[/insert_php]','Logging page','','inherit','closed','closed','','51-revision-v1','','','2018-08-23 17:40:43','2018-08-23 08:40:43','',51,'http://143.248.57.144/index.php/2018/08/23/51-revision-v1/',0,'revision','',0);
INSERT INTO `wp_posts` VALUES (526,48,'2018-08-23 17:41:15','2018-08-23 08:41:15','<b>Log level: debug</b>\r\n<br/><br/>\r\n[insert_php]\r\n\r\n$directory = \"/var/mms/logs/\";\r\n\r\n$myfile = fopen($directory.\"mms-server.log\", \"r\") or die(\"Unable to open file!\");\r\necho fread($myfile,filesize(\"/var/mms/logs/mms-server.log\"));\r\nfclose($myfile);\r\n\r\n[/insert_php]','Logging page debug','','inherit','closed','closed','','89-revision-v1','','','2018-08-23 17:41:15','2018-08-23 08:41:15','',89,'http://143.248.57.144/index.php/2018/08/23/89-revision-v1/',0,'revision','',0);
INSERT INTO `wp_posts` VALUES (527,48,'2018-08-23 17:41:34','2018-08-23 08:41:34','<b>Log level: warn</b>\r\n<br/><br/>\r\n[insert_php]\r\n\r\n$directory = \"/var/mms/logs/\";\r\n\r\n$myfile = fopen($directory.\"mms-server.log\", \"r\") or die(\"Unable to open file!\");\r\nif ($myfile ) {\r\nwhile (($line = fgets($myfile )) !== false) {\r\n// process the line read.\r\n\r\nif ((strpos($line, \'WARN\') !== false) || (strpos($line, \'ERROR\') !== false)) { echo $line; }\r\n}\r\n\r\nfclose($myfile );\r\n} else {\r\n// error opening the file.\r\n}\r\n\r\n[/insert_php]','Logging page warn','','inherit','closed','closed','','100-revision-v1','','','2018-08-23 17:41:34','2018-08-23 08:41:34','',100,'http://143.248.57.144/index.php/2018/08/23/100-revision-v1/',0,'revision','',0);
INSERT INTO `wp_posts` VALUES (532,48,'2018-08-23 17:55:50','2018-08-23 08:55:50','<table>\r\n<tbody>\r\n<tr>\r\n<td><a style=\"font-size: 12pt;\">The ID consuming realtime log service now. ID=</a><input id=\"id-consuming-realtime-log-svc\" type=\"text\" [urlparam attr=\"value\" param=\"id-consuming-realtime-log-svc\" default=\"\" /]/>&nbsp;&nbsp;<button id=\"id-consuming-realtime-log-svc-submit\" type=\"button\">view logs</button>&nbsp;&nbsp;<button id=\"id-consuming-realtime-log-svc-pause\" type=\"button\">pause</button>&nbsp;&nbsp;<button id=\"id-consuming-realtime-log-svc-reset\" type=\"button\">reset</button><br/>\r\nOnly the logs for the ID are shown.<br/>\r\nFirst, the ID must be registered in <strong>Realtime log service consumer IDs</strong>.<br/>\r\nIn order to regiser a ID in <strong>Realtime log service consumer IDs</strong>, use the function in <strong>the managing panel</strong> in <strong><a href=\"/index.php/mms-status-page/\">MMS Status Page</a>.</br>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table>\r\n<tbody>\r\n<tr>\r\n<td>\r\n<div id=\"realtime-log-container\"></div>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<div id=\"invisible-container\" style=\"display: none;\"></div>','MMS Realtime Log Page','','inherit','closed','closed','','409-revision-v1','','','2018-08-23 17:55:50','2018-08-23 08:55:50','',409,'http://143.248.57.144/index.php/2018/08/23/409-revision-v1/',0,'revision','',0);
